const r={get:e=>{const t=localStorage.getItem(e);return{[e]:t?JSON.parse(t):null}},set:e=>{Object.keys(e).forEach(t=>{localStorage.setItem(t,JSON.stringify(e[t]))})},remove:e=>{localStorage.removeItem(e)}};async function c(e){return r.remove(e)}async function n(e,t){try{return typeof t=="object"&&(t=JSON.parse(JSON.stringify(t))),r.set({[e]:t})}catch(o){console.log("set item error",o)}}async function s(e){try{return(await r.get(e))[e]}catch(t){console.log("get item error",t)}}export{s as getItem,c as removeItem,n as setItem};
